define view entity YI_PYM_DDL_CREATE_PAYMENT_BASE
  as select from    YI_PYM_DDL_BSIK       as bsik
//    left outer join YI_BusinessPartnerBank as BPBank on BPBank.BusinessPartner = bsik.Supplier
//    left outer join I_Bank_2 as Bank on Bank.BankCountry = BPBank.BankCountryKey
//                                    and Bank.BankInternalID = BPBank.BankNumber
//    left outer join ypym_t_payment  as payment on payment.accountingdocument     = bsik.AccountingDocument
//                                              and payment.fiscalyear             = bsik.FiscalYear
//                                              and payment.accountingdocumentitem = bsik.AccountingDocumentItem
    left outer join I_CompanyCode as T001 on T001.CompanyCode = bsik.CompanyCode                                                                              
{
  key bsik.CompanyCode,
  key bsik.Supplier,
  key bsik.SpecialGLTransactionType,
  key bsik.SpecialGLCode,
  key bsik.ClearingDate,
  key bsik.ClearingJournalEntry,
  key bsik.AssignmentReference,
  key bsik.FiscalYear,
  key bsik.AccountingDocument,
  key bsik.AccountingDocumentItem,
      bsik.DueCalculationBaseDate,
      bsik.CashDiscount1Days,
      bsik.PostingDate,
      bsik.DocumentDate,
      bsik.DebitCreditCode,
      bsik.AccountingDocumentType,
      bsik.AccountingDocCreatedByUser,
      @Semantics: { amount : {currencyCode: 'TransactionCurrency'} }
      bsik.AbsoluteAmountInTransacCrcy,
      bsik.TransactionCurrency,
      @Semantics: { amount : {currencyCode: 'CompanyCodeCurrency'} }
      bsik.AbsoluteAmountInCoCodeCrcy,
      bsik.CompanyCodeCurrency,
      @Semantics: { amount : {currencyCode: 'AdditionalCurrency1'} }
      bsik.AbsltAmtInAdditionalCurrency1,
      bsik.AdditionalCurrency1,
      @Semantics: { amount : {currencyCode: 'AdditionalCurrency2'} }
      bsik.AbsltAmtInAdditionalCurrency2,
      bsik.AdditionalCurrency2,
      @Semantics: { amount : {currencyCode: 'TransactionCurrency'} }
      bsik.AbsoluteAmountInTransacCrcy as paymentamount,      
      bsik.DocumentItemText,
      bsik.PaymentMethod,
      bsik.PurchasingDocument,
      bsik.PaymentBlockingReason,
      bsik.PaymentTerms,
      bsik.BusinessArea,
      bsik.AbsoluteExchangeRate,
      bsik.OriginalReferenceDocument,
      bsik.ParkedByUser,
       @ObjectModel.virtualElementCalculatedBy: 'ABAP:YCL_PYM_CREATE_PAYMENT_DATA'
//      BPBank.BankCountryKey as SupplierBankCountryKey,
      '' as SupplierBankCountryKey,
      @ObjectModel.virtualElementCalculatedBy: 'ABAP:YCL_PYM_CREATE_PAYMENT_DATA'
//      BPBank.BankNumber as SupplierBankNumber,
      '' as SupplierBankNumber,
      @ObjectModel.virtualElementCalculatedBy: 'ABAP:YCL_PYM_CREATE_PAYMENT_DATA'
//      BPBank.BankAccount as SupplierBankAccount,
      '' as SupplierBankAccount,
      @ObjectModel.virtualElementCalculatedBy: 'ABAP:YCL_PYM_CREATE_PAYMENT_DATA'
//      BPBank.IBAN as SupplierIBAN,
      '' as SupplierIBAN,
      @ObjectModel.virtualElementCalculatedBy: 'ABAP:YCL_PYM_CREATE_PAYMENT_DATA'
//      BPBank.SWIFTCode as SupplierSWIFTCode,
      '' as SupplierSWIFTCode,
      @ObjectModel.virtualElementCalculatedBy: 'ABAP:YCL_PYM_CREATE_PAYMENT_DATA'
//      BPBank.BankName as SupplierBankName,
      '' as SupplierBankName,
      @ObjectModel.virtualElementCalculatedBy: 'ABAP:YCL_PYM_CREATE_PAYMENT_DATA'
//      Bank.Branch as SupplierBankBrunch,
      '' as SupplierBankBrunch,
      @ObjectModel.virtualElementCalculatedBy: 'ABAP:YCL_PYM_CREATE_PAYMENT_DATA'
//      payment.paymentnumber,
      '' as paymentnumber,
      T001.Currency as CompanyCurrency
}
